package com.martin.rayate.viewsimport android.content.Intentimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport com.martin.rayate.Rimport com.martin.rayate.extensions.Extensions.toastimport com.martin.rayate.utils.FireBaseUtils.firebaseAuthimport kotlinx.android.synthetic.main.activity_home.*class HomeActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_home)// sign out a user        btnSignOut.setOnClickListener {            firebaseAuth.signOut()            startActivity(Intent(this, CreateAccountActivity::class.java))            toast("sesion cerrada")            finish()        }    }}/**class HomeActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_home)    }} */